//去重函数int REMOVE_DUPLICATE(int n,int array[]) &#123;	int i,j,k=n,m;	for (i = 0; i &lt; k; i++) &#123;		if (array[i] == -1)continue;		for(j=i+1;j&lt;k;j++)			if (array[i] == array[j]) &#123;					array[j] = -1;					counter++;			&#125;	&#125;	return 0;&#125;


