//排序函数void SEQUENCE(int n, int array[]) &#123;	int min = 0;	for (int i = 0; i &lt; n; i++)	&#123;		for (int j = i + 1; j &lt; n; j++)		&#123;			if (array[j] &lt; array[i])			&#123;				min = array[j];				array[j] = array[i];				array[i] = min;			&#125;		&#125;	&#125;&#125;


